mapper.select:
SELECT *
FROM boards
WHERE category = #{category}

	AND subject LIKE CONCAT('%', #{search}, '%')

ORDER BY re_ref DESC, re_seq ASC
LIMIT #{pageSize} OFFSET #{startRow}

mapper.update:
UPDATE boards
SET subject = #{subject}, content =	#{content}
WHERE num = #{num}

UPDATE boards
SET re_seq = re_seq + 1
WHERE re_ref =
#{reRef}
AND re_seq > #{reSeq}
